#include <bits/stdc++.h>using namespace std;int main(){	ios_base::sync_with_stdio(false);	cin.tie(NULL);		int t;	cin >> t;	while(t-- > 0)	{		int a[3];		int b[3];		cin >> a[0] >> a[1] >> a[2];		cin >> b[0] >> b[1] >> b[2];		int a_stat = 0, b_stat = 0;		for(int i = 0 ; i < 3 ; i++)		{			if(a[i] > b[i])				a_stat++;			else				b_stat++;		}		if(a_stat > b_stat)		cout << "A" << "\n";		else		cout << "B" << "\n";	}	return 0;}