#include <bits/stdc++.h>using namespace std;int chkmax(int a , int b){	if(a < b)	return b;	else	return a;}int count(char c, string& a){	int ans = 0;	for(auto i : a)	{		if(i == c)		++ans;		else		--ans;	}	return ans;}int calc(vector<string>& a, char c){	vector<int> counter;	for(auto i : a)	{		counter.push_back(count(c,i));	}	sort(counter.begin(),counter.end());	reverse(counter.begin(),counter.end());	int ans = 0;	int cnt = 0; // final number of strings used	for(auto i : counter)	{		if(ans + i > 0)		{			ans += i;			++cnt;		}		else		{			break;		}	}	return cnt;}void solve(){	int n;	cin >> n;	vector<string> a(n);	for(auto& i: a)	{		cin >> i;	}	int ans = 0;	for(char c = 'a' ; c <= 'e' ; ++c)	{		ans = chkmax(ans, calc(a,c));	}	cout << ans << "\n";}int main(){	int t;	cin >> t;	while(t-- > 0)	{		solve();	}	return 0;}