#include <bits/stdc++.h>using namespace std;int main(){	ios_base::sync_with_stdio(false);	cin.tie(NULL);		int t;	cin >> t;	while(t-- > 0)	{		int n;		cin >> n;		vector<int> arr;		int c1,c2;		// c1 + 2c2 = n		// n - c1 is even		// even - even = even 		// odd - odd = even		if(n%3 == 0)			cout << n/3 << " " << (n/3) << "\n";		else		{			//int ans = ceil((double)n/3) - floor((double)n/3);			int cel = ceil((double)n/3);			int flo =floor((double)n/3);			if(cel + 2*flo == n)			cout << cel << " " << flo << "\n";			else			cout << flo << " " << cel << "\n";				}			}	return 0;}